function save_options(){var e=document.getElementById("alc-save-url");chrome.storage.sync.set({alcSaveUrl:e.value},function(){var e=document.getElementById("status");e.textContent="Options saved.",setTimeout(function(){e.textContent=""},750)})}function restore_options(){var e=document.getElementById("alc-save-url");chrome.storage.sync.get({alcSaveUrl:"Please input server url."},function(t){e.value=t.alcSaveUrl})}document.addEventListener("DOMContentLoaded",restore_options),document.getElementById("save").addEventListener("click",save_options);